mapper.insert:

    select nvl(max(imgBoardId), 0)+1 from imgBoard

insert into imgBoard
(
imgBoardId
,title
,writer
,filename
,filepath
,createdDate
)
values
(
#{imgBoardId}
,#{title}
,#{writer}
,#{filename}
,#{filepath}
,sysdate
)

mapper.update:
update imgboard
set
    title = #{title}
    ,filename = #{filename}
    ,filepath = #{filepath}
    ,createdDate = sysdate
where imgBoardId = #{imgBoardId}
